package practice.java;

class CountIndex
{
	 int count;
	 int index;
	 
	 //Initialization for 1st occurrence of character
	 public CountIndex(int index)
	 {
		 this.count = 1;
		 this.index = index;
	 }
	 
	 public void incCount()
	 {
		 this.count++;
	 }
	 	 
}


